INSERT INTO `doctor` VALUES (1,'吴瑞丰','复旦大学附属华山医院','166','擅长：高血压，高血脂，头昏，脑供血不足等','主治医师','血液内科','99%','http://img2.imgtn.bdimg.com/it/u=3587461461,2161693707&fm=26&gp=0.jpg'),(2,'田浩','复旦大学附属华山医院','122','擅长：糖尿病及其并发症，内分泌相关的高血压，甲状腺疾病','主治医师','内分泌科','98%','https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=349537747,1656069851&fm=26&gp=0.jpg'),(3,'邓坤','中国人民解放军总医院301医院','133','擅长：长期从事老年心血管病临床工作，对老年人高血压，冠心病等经验丰富','主治医师','神经内科','100%','https://ss3.bdstatic.com/70cFv8Sh_Q1YnxGkpoWK1HF6hhy/it/u=1806333936,556334769&fm=26&gp=0.jpg'),(4,'王海洋','北京协和医院','177','擅长：高血压、冠心病、心衰、高脂血症诊治、药物治疗','住院医师','血液内科','98%','http://img0.imgtn.bdimg.com/it/u=2554563746,878069854&fm=26&gp=0.jpg'),(5,'朱海聪','南方医科大学南方医院','178','擅长：擅长糖尿病、高血压血脂异常、高尿酸血症、骨质疏松症等慢性病的预防与治疗','主任医师','内分泌科','98%','http://img2.imgtn.bdimg.com/it/u=4228025350,1027142438&fm=26&gp=0.jpg'),(6,'李欣','南方医科大学南方医院','166','擅长：内分泌系统疾病诊治，包括各类糖尿病，甲状腺疾病，高血压，痛风，皮质醇增多症','副主任医师','心血管内科','98%','http://img3.imgtn.bdimg.com/it/u=681678428,2560066729&fm=26&gp=0.jpg'),(7,'傅谦','泰安市中心医院','133','擅长：糖尿病及其并发症，内分泌疾病','住院医师','内科','99%','http://img5.imgtn.bdimg.com/it/u=1936293975,1271775086&fm=26&gp=0.jpg'),(8,'测试','33333333',NULL,NULL,'33333333333333','333333',NULL,NULL),(9,'林俊杰','邵逸夫医院',NULL,NULL,NULL,'血液内科',NULL,NULL),(10,'彭于晏','邵逸夫医院',NULL,NULL,'主任医师','血液内科',NULL,NULL);
